int exitX = 91, exitY = 248;int exitdx = 586, exitdy = 234;int areusureX = 378, areusureY = 439;int horBarGap = 111;int yesbarx = 153, yesbary = 282;int chotobardx = 172, chotobardy = 60;void resetToNewGame();/* to be called inside iDraw. Draws a bar of dx length and dy height from the point x,y   if cursor hovers over the bar postion, the value of selected becomes 1, 0 otherwise.   when the value of selected is 1, the bar pops up by sizek size. */void drawBar(double x, double y, double dx, double dy, int sizek, int selected){    double xp = x-sizek*selected;    double yp = y-sizek*selected;    double dxp = dx+2*sizek*selected;    double dyp = dy+2*sizek*selected;    iFilledRectangle(xp, yp, dxp, dyp);    iFilledCircle(xp, yp + dyp/2, dyp/2);    iFilledCircle(xp + dxp, yp + dyp/2, dyp/2);}/* checks if the bar is selected for mouse position at mx, my */int isBarSelected(int mx, int my, int barx, int bary, int bardx, int bardy){    int ret = 0;    if(barx<=mx && mx<=barx+bardx && bary<=my && my<=bary+bardy) ret = 1;    else if(dis(mx,my, barx,bary+bardy/2) <= bardy/2) ret = 1;    else if(dis(mx,my, barx+bardx,bary+bardy/2) <= bardy/2) ret = 1;    else ret = 0;    return ret;}/* draws four parallel bars. Called in iDraw() */void drawMenu(double x,double y,double dx,double dy, double k){    int i, popUp = 0;    for(i=0;i<=3;i++)    {        double xp = x;        double yp = y+i*(k+dy);        popUp = isBarSelected(Mx,My, xp, yp, menuBardx, menuBardy);        iSetColor(77,195,255);        drawBar(xp, yp, dx, dy, popUpSize, popUp);        iSetColor(0, 0, 230);        int fnt = (popUp) ? 4 : 3;        if(i == 3) RetroMiddleText(xp+dx/2, yp+dy/2, "NEW GAME", fnt);        else if(i == 2) RetroMiddleText(xp+dx/2, yp+dy/2, "HIGHSCORES", fnt);        else if(i == 1)RetroMiddleText(xp+dx/2, yp+dy/2, "CREDITS", fnt);        else RetroMiddleText(xp+dx/2, yp+dy/2, "EXIT", fnt);    }}/* draws the exit screen */void drawExit(){    iSetColor(240, 240, 240);    iFilledRectangle(exitX, exitY, exitdx, exitdy);    iSetColor(77, 195, 255);    RetroMiddleText(areusureX, areusureY, "ARE YOU SURE?", 6);    int selected = isBarSelected(Mx, My, yesbarx, yesbary, chotobardx, chotobardy);    drawBar(yesbarx, yesbary, chotobardx, chotobardy, 6, selected);    iSetColor(0, 0, 230);    int tempfnt = (selected) ? 4 : 3;    RetroMiddleText(yesbarx+chotobardx/2, yesbary+chotobardy/2, "YES", tempfnt);    selected = isBarSelected(Mx, My, yesbarx+chotobardx+horBarGap, yesbary, chotobardx, chotobardy);    iSetColor(77, 195, 255);    drawBar(yesbarx+chotobardx+horBarGap, yesbary, chotobardx, chotobardy, 6, selected);    iSetColor(0, 0, 230);    tempfnt = (selected) ? 4 : 3;    RetroMiddleText(yesbarx+chotobardx+horBarGap+chotobardx/2, yesbary+chotobardy/2, "NO", tempfnt);}void drawPERATRICKS(){    iSetColor(191, 255, 0);	RetroMiddleText( 400, 584, "P         ", 10);    iSetColor(255, 51, 51);	RetroMiddleText( 400, 584, " E        ", 10);	iSetColor(0, 80, 255);	RetroMiddleText( 400, 584, "  R       ", 10);	iSetColor(0, 255, 255);	RetroMiddleText( 400, 584, "   A      ", 10);	iSetColor(100, 200, 55);	RetroMiddleText( 400, 584, "    T     ", 10);	iSetColor(150, 250, 0);	RetroMiddleText( 400, 584, "     R    ", 10);	iSetColor(100, 50, 255);	RetroMiddleText( 400, 584, "      I   ", 10);	iSetColor(207, 80, 195);	RetroMiddleText( 400, 584, "       C  ", 10);	iSetColor(255, 165, 0);	RetroMiddleText( 400, 584, "        K ", 10);	iSetColor(137, 51, 219);	RetroMiddleText( 400, 584, "         S", 10);}/* checks if the user wants to exit or not */void checkExitOption(int mx, int my){    int yes = isBarSelected(Mx, My, yesbarx, yesbary, chotobardx, chotobardy);    if(yes){        if(GAMESTATE == GAMEMENUEXIT) exit(0);        if(GAMESTATE == GAMEPAUSEMENU)            PlaySound(TEXT("DOCTOR VOX - Hero.wav"), NULL, SND_LOOP | SND_ASYNC),            GAMESTATE = GAMEMENU;    }    int no = isBarSelected(Mx, My, yesbarx+chotobardx+horBarGap, yesbary, chotobardx, chotobardy);    if(no){        if(GAMESTATE == GAMEMENUEXIT) GAMESTATE = GAMEMENU;        else if(GAMESTATE == GAMEPAUSEMENU) GAMESTATE = GAMEPLAYING;    }}/* Called when the user left clicks on the mouse. The function checks if user has pressed any   valid option from the menu, and if so, then executes that command. */void checkMenuOption(double mx, double my){    int i, valid = 0;    int k = menuBarGap, dx =  menuBardx, dy = menuBardy;    for(i=0;i<=3;i++)    {        double xp = X;        double yp = Y+i*(k+dy);        valid = isBarSelected(Mx, My, xp, yp, menuBardx, menuBardy);        if(!valid) continue;        if(i == 3) gameTime = 0, resetToNewGame();        else if(i == 2) loadHighScores(), GAMESTATE = GAMEHIGHSCORE;        else if(i == 1) GAMESTATE = GAMEHELP;        else GAMESTATE = GAMEMENUEXIT;    }}void drawHelp(){    iSetColor(140, 26, 255);    RetroMiddleText(400, 588+30, "CREDITS", 8);    int gaaap = nameGap - 8;    int yyy = 588-75;    iSetColor(166, 77, 255);    RetroMiddleText(400, yyy, "SUBMITTED TO", 4);    iSetColor(217, 179, 255);    RetroMiddleText(400, yyy-gaaap, "DR MD SHOHRAB HOSSAIN", 3);    RetroMiddleText(400, yyy-2*gaaap, "ASSOCIATE PROFESSOR", 3);    RetroMiddleText(400, yyy-3*gaaap, "DEPT OF CSE BUET", 3);    iSetColor(166, 77, 255);    yyy = yyy-3*gaaap-65;    RetroMiddleText(400, yyy, "SUBMITTED BY", 4);    iSetColor(217, 179, 255);    RetroMiddleText(400, yyy-1*gaaap, "TASEEN MUBASSIRA 065", 3);    RetroMiddleText(400, yyy-2*gaaap, "SABBIR RAHMAN 076", 3);    RetroMiddleText(400, yyy-3*gaaap, "MEHEDI HASAN 085", 3);    int backX = 314, backY = 100;    int backdx = 172, backdy = 60;    int selected = isBarSelected(Mx, My, backX, backY, backdx, backdy);    iSetColor(179, 102, 255);    drawBar(backX, backY, backdx, backdy, 6, selected);    int tempfnt = (selected) ? 4 : 3;    iSetColor(230, 230, 230);    RetroMiddleText(backX+backdx/2, backY+backdy/2 , "BACK", tempfnt);}